/**
 * @public
 */
type ExploreTool = {
    title: string;
    description?: string;
    url: string;
    image: string;
    tags?: string[];
    lifecycle?: string;
};

/**
 * @public
 */
type ExploreToolFilter = {
    /**
     * Filter by a list of tags
     */
    tags?: string[];
    /**
     * Filter by a list of lifecycle strings
     */
    lifecycle?: string[];
};
/**
 * @public
 */
type GetExploreToolsRequest = {
    filter?: ExploreToolFilter;
};
/**
 * @public
 */
type GetExploreToolsResponse = {
    tools: ExploreTool[];
};

export type { ExploreTool, ExploreToolFilter, GetExploreToolsRequest, GetExploreToolsResponse };
